import React, { useEffect } from 'react';
import { Button, Col, Row } from 'react-bootstrap';
import "../css/About.css";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import styled from "styled-components";
import Slider from "react-slick";

const Carousel = styled(Slider)`
  // height: 30vh;
  // overflow: hidden;

  & > button {
    opacity: 1;
    height: 100%;
    width: 15vw;
    z-index: 1;

    &:hover {
      opacity: 1;
      transition: opacity 0.2s ease 0s;
    }
  }

  ul li button {
    &:before {
      top: -3vh;
      font-size: 20px;
      color: gray;
      left: -35px;
    }
  }

  li.slick-active button:before {
    color: #e9af84;
  }

  .slick-list {
    overflow: initial;
  }

  .slick-prev {
    left: -75px;
    width: 12vw;
    height: 100%;
    padding-left: 4%;
  }

  .slick-prev:before {
    color: #e9af84;
    font-size: 50px;
  }

  .slick-next {
    right: -75px;
    width: 12vw;
    height: 100%;
    padding-right: 4%;
  }

  .slick-next:before {
    color: #e9af84;
    font-size: 50px;
  }
`;

const Wrap = styled.div`
border-radius: 4px;
cursor: pointer;
position: relative;


&:hover {
  padding: 0;
  // border: 4px solid rgba(249, 249, 249, 0.8);
  transition-duration: 300ms;
}
}
`;

const About = () => {
  let settings = {
    dots: true,
    infinite: true,
    speed: 500,
    autoplay: false,
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 1,
          infinite: true,
          dots: false,
        },
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
          initialSlide: 1,
        },
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
        },
      },
    ],
  };
  return (
    <>
      <div id="hero">
        <div className='texture'>
        </div>
        <div className='caption-about'>
          <h1>accessed destinations</h1>
          <div style={{ display: "inline-flex" }}>
            <Carousel {...settings}>
              <Wrap>
                <Button className="box-1">
                  New Haven, CT
                </Button>  <Button className='box-2'>
                  Manhattan, NY
                </Button>
              </Wrap>
            </Carousel>

          </div>
          <h1>past shared locations</h1>
        </div>
      </div>
      <div className='mt-5'>
        <Col md={12} className="m-auto pt-2">
          <Row>
            <Carousel {...settings}>
              <Wrap>
                <Col md={12} style={{ marginBottom: "30px" }}>
                  <div className="box-3"></div>
                </Col>
                <Col md={12} style={{ marginBottom: "30px" }}>
                  <div className='box-4'></div>
                </Col>
              </Wrap>
            </Carousel>
          </Row>
        </Col>

        <Row>
          <Col>
            Wisp is a travel app that suggests locations/activities based on
            uniqueness, affordability, and interests, not popularity. User
            crowdsourced. For the new generation.
          </Col>
        </Row>
      </div>
    </>
  )
}



export default About;
