import { Button, Col, Row, Carousel, Modal } from 'react-bootstrap';
import "../css/About.css";
import token from '../img/spirit_token.png';
import 'bootstrap/dist/css/bootstrap.min.css';
// import "slick-carousel/slick/slick.css";
